mapper.select:
select * from problem
where unit = #{userUnit}
ORDER BY time desc LIMIT #{page},#{number}

select * from problem where name like #{remark}
union
select * from problem where title like #{remark}
union
select * from problem where content like  #{remark}
 union
select * from problem where unitName like #{remark}
union
select * from problem where problemType like #{remark}
union
select * from problem where type like #{remark}
ORDER BY time LIMIT #{page},#{number}

select * from problem
where unit = -1 and selectKey like #{remark}
ORDER BY time desc LIMIT #{page},#{number}

select * from problem
where unit != -1 and selectKey like #{remark}
ORDER BY time desc LIMIT #{page},#{number}
